import 'mocha';
import {expect} from 'chai';
import {TezosLedgerWallet} from '../src/identity/tezos/TezosLedgerWallet';

describe('getTezosPublicKey()', () => {
    it('should correctly fetch the root key', async () => {
        const result = await TezosLedgerWallet.getTezosPublicKey("44'/1729'/0'/0'/0'");

        expect(result).to.be.a('string')
    });
});

describe('signTezosOperation()', () => {
    it('should correctly sign an operation', async () => {
        const result = await TezosLedgerWallet.signTezosOperation(
            "44'/1729'/0'/0'/0'",
            '');

        expect(result).to.be.a('Uint8Array')
    });
});


